mapper.insert:
INSERT INTO MESSAGE ( MSG_NO, MSG_TO, MSG_FROM, MSG_CONTENT, CREATE_DATE, DELETE_DATE, READ_DATE, READ_STATUS, STATUS, SEND_STATUS ) SELECT SEQ_MSG_NO.NEXTVAL AS MSG_NO , T.* FROM(
SELECT #{item.msgTo} AS MSG_TO, #{item.msgFrom} AS MSG_FROM, #{item.msgContent} AS MSG_CONTENT, SYSDATE AS CREATE_DATE, SYSDATE AS DELETE_DATE, SYSDATE AS READ_DATE, 'N' AS READ_STATUS, 'Y' AS STATUS, 'Y' AS SEND_STATUS FROM DUAL
) T

mapper.select:
SELECT STATUS
FROM PROJECT
WHERE PROJECT_NO = #{proNum}
